import React, { useState, useEffect } from "react";
import urlService from "../services/api";
import { toast } from "react-toastify";

const Home = () => {
  const [url, setUrl] = useState("");
  const [expiry, setExpiry] = useState("");
  const [urls, setUrls] = useState([]);
  const [status, setStatus] = useState("offline");

  // âœ… Load URLs on mount
  useEffect(() => {
    fetchUrls();
    checkBackendStatus();
  }, []);

  // ðŸ”¹ Fetch all URLs
  const fetchUrls = async () => {
    try {
      const data = await urlService.getAllUrls();
      setUrls(data);
    } catch (error) {
      toast.error("Failed to fetch URLs");
    }
  };

  // ðŸ”¹ Check backend health
  const checkBackendStatus = async () => {
    try {
      const health = await urlService.healthCheck();
      setStatus(health.status === "UP" ? "online" : "offline");
    } catch {
      setStatus("offline");
    }
  };

  // ðŸ”¹ Shorten URL
  const handleShorten = async () => {
    if (!url.trim()) {
      toast.warn("Please enter a valid URL!");
      return;
    }
    try {
      const data = await urlService.shortenUrl(url, expiry);
      toast.success("URL shortened successfully!");
      setUrls([...urls, data]);
      setUrl("");
      setExpiry("");
    } catch (error) {
      toast.error(error.message);
    }
  };

  // ðŸ”¹ Delete URL
  const handleDelete = async (shortCode) => {
    try {
      await urlService.deleteUrl(shortCode);
      toast.info("Deleted successfully!");
      setUrls(urls.filter((item) => item.shortCode !== shortCode));
    } catch (error) {
      toast.error(error.message);
    }
  };

  return (
    <div className="min-h-screen flex flex-col items-center justify-center bg-gray-50 p-4">
      <div className="bg-white shadow-lg rounded-2xl p-6 w-full max-w-lg">
        <h1 className="text-2xl font-bold text-center text-blue-600 mb-4">
          ðŸ”— URL Shortener{" "}
          <span
            className={`ml-2 text-sm px-2 py-1 rounded-full ${
              status === "online" ? "bg-green-100 text-green-600" : "bg-red-100 text-red-600"
            }`}
          >
            {status}
          </span>
        </h1>

        <input
          type="text"
          value={url}
          onChange={(e) => setUrl(e.target.value)}
          placeholder="Enter URL here..."
          className="w-full border rounded-lg p-2 mb-3"
        />
        <input
          type="datetime-local"
          value={expiry}
          onChange={(e) => setExpiry(e.target.value)}
          className="w-full border rounded-lg p-2 mb-4"
        />

        <button
          onClick={handleShorten}
          className="w-full bg-blue-600 text-white py-2 rounded-lg hover:bg-blue-700"
        >
          Shorten URL
        </button>

        <h2 className="text-lg font-semibold mt-6 mb-2">All URLs</h2>
        <div className="space-y-2">
          {urls.length === 0 ? (
            <p className="text-gray-500 text-sm text-center">No URLs found</p>
          ) : (
            urls.map((item) => (
              <div
                key={item.id}
                className="flex justify-between items-center border p-2 rounded-md"
              >
                <a
                  href={item.longUrl}
                  target="_blank"
                  rel="noopener noreferrer"
                  className="text-blue-500 underline"
                >
                  {item.shortCode}
                </a>
                <button
                  onClick={() => handleDelete(item.shortCode)}
                  className="text-red-500 font-semibold"
                >
                  Delete
                </button>
              </div>
            ))
          )}
        </div>
      </div>
    </div>
  );
};

export default Home;
