import React, { useState } from 'react';
import { toast } from 'react-toastify';
import { Link2, Copy, QrCode, Calendar, ExternalLink, Zap } from 'lucide-react';
import QRCode from 'react-qr-code';
import copy from 'copy-to-clipboard';
import { urlService } from '../services/api';


const Home = () => {
  const [originalUrl, setOriginalUrl] = useState('');
  const [expiryDate, setExpiryDate] = useState('');
  const [shortenedUrl, setShortenedUrl] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [showQR, setShowQR] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!originalUrl.trim()) {
      toast.error('Please enter a valid URL');
      return;
    }

    // Basic URL validation
    try {
      new URL(originalUrl);
    } catch {
      toast.error('Please enter a valid URL (include http:// or https://)');
      return;
    }

    setIsLoading(true);
    try {
      const result = await urlService.shortenUrl(originalUrl, expiryDate || null);
      setShortenedUrl(result.shortUrl);
      toast.success('URL shortened successfully! ðŸŽ‰');
    } catch (error) {
      toast.error(error.message);
    } finally {
      setIsLoading(false);
    }
  };

  const handleCopy = () => {
    if (copy(shortenedUrl)) {
      toast.success('Copied to clipboard! ðŸ“‹');
    } else {
      toast.error('Failed to copy');
    }
  };

  const handleReset = () => {
    setOriginalUrl('');
    setExpiryDate('');
    setShortenedUrl('');
    setShowQR(false);
  };

  // âœ… The rest of your JSX stays the same â€” no other changes needed
  return (
    <div className="max-w-4xl mx-auto">
      {/* ... all your JSX code here (same as before) ... */}
    </div>
  );
};

export default Home;
